import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import '../Components/Project.css';

const projects = [
    {
      title: 'HR Management Dashboard',
      type: 'Power BI Dashboard',
      description: 'A comprehensive HR management dashboard built with Power BI that highlights key metrics.',
      link: 'https://github.com/your-profile/hr-dashboard'
    },
    {
      title: 'Figma Designs',
      type: 'UI/UX Design',
      description: 'Explore my collection of Figma design projects.',
      projects: [
        { title: 'Mobile App Design', link: 'https://www.figma.com/file/your-design-link-1' },
        { title: 'Web Dashboard Design', link: 'https://www.figma.com/file/your-design-link-2' },
        { title: 'E-Commerce Concept', link: 'https://www.figma.com/file/your-design-link-3' }
      ]
    },
    {
      title: 'Machine Learning Case Studies',
      type: 'Case Studies',
      description: 'A collection of ML projects demonstrating data analysis and model building.',
      link: 'https://github.com/your-profile/ml-case-studies'
    }
];

const ProjectCard = ({ project }) => {
  const [expanded, setExpanded] = useState(false);

  const toggleCard = () => {
    setExpanded(prev => !prev);
  };

  // Function to open external link (for non-Figma projects)
  const openExternalLink = (e, url) => {
    e.stopPropagation();
    window.open(url, '_blank', 'noopener,noreferrer');
  };

  // Optional fallback image if undefined
  const projectImage = project.image || '/assets/default-project.png';

  return (
    <motion.div 
      className="project-card" 
      layout 
      onClick={toggleCard}
      initial={{ borderRadius: 10 }}
    >
      <motion.img 
        src={projectImage} 
        alt={project.title} 
        className="project-image" 
        layout 
      />
      <motion.h3 layout>{project.title}</motion.h3>
      <motion.p className="project-type" layout>{project.type}</motion.p>
      <AnimatePresence>
        {expanded && (
          <motion.div 
            className="project-details"
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
          >
            <motion.p>{project.description}</motion.p>
            {project.projects ? (
              <div className="figma-projects">
                {project.projects.map((figmaProj, index) => (
                  <motion.button 
                    key={index}
                    className="figma-project-btn"
                    onClick={(e) => openExternalLink(e, figmaProj.link)}
                    whileHover={{ scale: 1.05 }}
                  >
                    {figmaProj.title}
                  </motion.button>
                ))}
              </div>
            ) : (
              project.link && (
                <motion.button
                  className="view-project-btn"
                  onClick={(e) => openExternalLink(e, project.link)}
                  whileHover={{ scale: 1.05 }}
                >
                  Learn More
                </motion.button>
              )
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default ProjectCard;
