import React, { useState } from 'react';
import { FaEnvelope, FaLinkedin, FaGithub, FaPhone } from 'react-icons/fa';
import '../Css/Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });
  const [status, setStatus] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Replace <YOUR_FORMSPREE_ENDPOINT> with your actual endpoint URL from Formspree.
    const endpoint = "https://formspree.io/f/<YOUR_FORMSPREE_ENDPOINT>";

    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: {
          Accept: "application/json"
        },
        body: new URLSearchParams(formData)
      });

      if (response.ok) {
        setStatus("SUCCESS");
        setFormData({ name: "", email: "", message: "" });
      } else {
        setStatus("ERROR");
      }
    } catch (error) {
      setStatus("ERROR");
    }
  };

  return (
    <section id="contact" className="contact-section">
      <div className="contact-container">
        <div className="contact-form">
          <form onSubmit={handleSubmit}>
            <label htmlFor="name">Name:</label>
            <input 
              id="name" 
              type="text" 
              name="name" 
              value={formData.name} 
              onChange={handleChange} 
              required 
            />

            <label htmlFor="email">Email:</label>
            <input 
              id="email" 
              type="email" 
              name="email" 
              value={formData.email} 
              onChange={handleChange} 
              required 
            />

            <label htmlFor="message">Message:</label>
            <textarea 
              id="message" 
              name="message" 
              value={formData.message} 
              onChange={handleChange} 
              required 
            />

            <button type="submit">Send Message</button>
          </form>
          {status === "SUCCESS" && <p className="success-msg">Thanks, your message has been sent!</p>}
          {status === "ERROR" && <p className="error-msg">Oops, there was an error. Please try again!</p>}
        </div>
        <div className="contact-info">
          <h3>Get in Touch</h3>
          <ul>
            <li>
              <FaEnvelope className="contact-icon" />
              <a href="mailto:your-email@example.com">your-email@example.com</a>
            </li>
            <li>
              <FaPhone className="contact-icon" />
              <span>+1-555-123-4567</span>
            </li>
            <li>
              <FaLinkedin className="contact-icon" />
              <a href="https://linkedin.com/in/your-profile" target="_blank" rel="noopener noreferrer">LinkedIn</a>
            </li>
            <li>
              <FaGithub className="contact-icon" />
              <a href="https://github.com/your-profile" target="_blank" rel="noopener noreferrer">GitHub</a>
            </li>
          </ul>
        </div>
      </div>
    </section>
  );
};

export default Contact;
