import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaLinkedin, FaGithub, FaEnvelope, FaPhone } from 'react-icons/fa';
import '../Css/Contact.css';

const AnimatedContact = () => {
  const [flipped, setFlipped] = useState(false);

  const handleFlip = () => setFlipped((prev) => !prev);

  return (
    <section id="contact" className="animated-contact-section">
      <h2>Get in Touch</h2>
      <motion.div 
        className={`contact-card ${flipped ? 'flipped' : ''}`}
        whileHover={{ scale: 1.05 }}
        transition={{ type: "spring", stiffness: 300 }}
        onClick={handleFlip}
      >
        <div className="card-face card-front">
          <h3>Contact Me</h3>
          <p>Click here for details</p>
        </div>
        <div className="card-face card-back">
          <div className="contact-details">
            <p>
              <FaEnvelope className="contact-icon" /> your-email@example.com
            </p>
            <p>
              <FaPhone className="contact-icon" /> +1-555-123-4567
            </p>
            <div className="social-links">
              <a href="https://linkedin.com/in/your-profile" target="_blank" rel="noopener noreferrer">
                <FaLinkedin className="social-icon" />
              </a>
              <a href="https://github.com/your-username" target="_blank" rel="noopener noreferrer">
                <FaGithub className="social-icon" />
              </a>
            </div>
          </div>
        </div>
      </motion.div>
    </section>
  );
};

export default AnimatedContact;
