import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaTimes, FaLinkedin, FaGithub, FaEnvelope, FaPhone } from 'react-icons/fa';
import '../Components/Contact.css';

const SlidingContact = () => {
  const [open, setOpen] = useState(false);

  const slideVariants = {
    hidden: { x: '100%' },
    visible: { x: 0 },
    exit: { x: '100%' }
  };

  return (
    <>
      <button className="open-contact-btn" onClick={() => setOpen(true)}>
        Contact
      </button>
      <AnimatePresence>
        {open && (
          <motion.div
            className="contact-overlay"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={slideVariants}
            transition={{ type: 'spring', stiffness: 300, damping: 30 }}
          >
            <div className="contact-content">
              <button className="close-btn" onClick={() => setOpen(false)}>
                <FaTimes />
              </button>
              <h2>Get in Touch</h2>
              <p>
                I'd love to hear from you! Please reach out for collaborations or just a friendly chat.
              </p>
              <div className="contact-links">
                <a href="mailto:your-email@example.com">
                  <FaEnvelope className="icon" />
                </a>
                <a href="tel:+15551234567">
                  <FaPhone className="icon" />
                </a>
                <a
                  href="https://linkedin.com/in/your-profile"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <FaLinkedin className="icon" />
                </a>
                <a
                  href="https://github.com/your-username"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <FaGithub className="icon" />
                </a>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
};

export default SlidingContact;
